package Day6_math;

public class shorthandOperator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int students = 45;
		students = students +5;
	
		System.out.println(students);
		
		int teachers=10;
		teachers+=2;
		System.out.println(teachers);
		
		int cars = 12;
		cars*=2; //cars = cars*2
		System.out.println(cars);
		
		
		

		
		

	}

}
